import { __decorate } from "tslib";
import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Subject } from 'rxjs';
import { environment } from 'src/environments/environment';
let CartService = class CartService {
    constructor(http) {
        this.http = http;
        this.items = [];
        this.subject = new Subject();
    }
    getAll() {
        return this.http.get(`${environment.apiURL}/products.json`);
    }
    getById(phoneId) {
        return this.http.get(`${environment.apiURL}/products/${phoneId}.json`);
    }
    addToCart(product) {
        this.items.push(product);
        this.toCountAllSum(this.items);
        this.subject.next(this.items.length);
    }
    getItems() {
        return this.items;
    }
    toCountAllSum(items) {
        this.sum = items.reduce((prev, { price }) => prev + price, 0);
    }
    removeFromBasket(item) {
        this.items = this.items.filter((i) => i.name !== item.name);
        this.toCountAllSum(this.items);
        this.subject.next(this.items.length);
    }
};
CartService.ctorParameters = () => [
    { type: HttpClient }
];
CartService = __decorate([
    Injectable({
        providedIn: 'root'
    })
], CartService);
export { CartService };
