import { __decorate } from "tslib";
import __NG_CLI_RESOURCE__0 from "./phone-details-page.component.html?ngResource";
import __NG_CLI_RESOURCE__1 from "./phone-details-page.component.scss?ngResource";
import { ChangeDetectionStrategy, ChangeDetectorRef, Component } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { CartService } from 'src/app/cart.service';
import { environment } from 'src/environments/environment';
import { map, switchMap } from 'rxjs/operators';
import { Location } from '@angular/common';
let PhoneDetailsPageComponent = class PhoneDetailsPageComponent {
    constructor(activatedRoute, cartService, location, cd) {
        this.activatedRoute = activatedRoute;
        this.cartService = cartService;
        this.location = location;
        this.cd = cd;
        this.phones = [];
    }
    ngOnInit() {
        this.activatedRoute.params
            .pipe(map(params => params['phoneId']), switchMap(phoneId => this.cartService.getById(phoneId)))
            .subscribe(details => {
            this.phone = details;
            this.cartService.getAll().subscribe(phones => {
                this.phones = phones;
                this.priceFromPhones = phones.find(item => item.id === this.phone.id);
                this.phone.price = this.priceFromPhones.price;
            });
            this.cd.markForCheck();
        });
    }
    getImageURL(url) {
        return `${environment.imgURL}${url}`;
    }
    addToCart(phone) {
        this.cartService.addToCart(phone);
        window.alert('Ваш товар додано у корзину!');
    }
    back() {
        this.location.back();
    }
};
PhoneDetailsPageComponent.ctorParameters = () => [
    { type: ActivatedRoute },
    { type: CartService },
    { type: Location },
    { type: ChangeDetectorRef }
];
